import { createSlice } from "@reduxjs/toolkit";
// Imported user reducer
// import { setUserId, setAccessToken } from './user';
import { API_URL } from "utils/urls";

export const recipe = createSlice({
  name: "recipe",
  initialState: {
    items: [],
    error: null,
  },
  reducers: {
    setItems: (store, action) => {
      store.items = action.payload;
    },
    setError: (store, action) => {
      store.error = action.payload;
    },
  },
});

// Thunk fetchTasks: getting all the users tasks

// Fetch user recipes for profile?????!!!! Import into Profile????! recipes/user/${userID}?
export const fetchRecipes = (accessToken, userId) => {
  return (dispatch) => {
    const options = {
      method: "GET",
      headers: {
        Authorization: accessToken,
      },
    };
    fetch(API_URL(`recipes/${userId}`), options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (data.success) {
          dispatch(recipe.actions.setItems(data.response));
          dispatch(recipe.actions.setError(null));
        } else {
          dispatch(recipe.actions.setItems([]));
          dispatch(recipe.actions.setError(data.response));
        }
      });
  };
};

// Fetch all user recipes. Same as above but just /recipes?


// Post new recipe??????!!! Import into CreateRecipes???
export const postTasks = (accessToken, userId, task) => {
  return (dispatch) => {
    const options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: accessToken,
      },
      body: JSON.stringify({ task, user: userId }),
    };
    fetch(API_URL("tasks/addtask"), options)
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          console.log(data);
          dispatch(fetchTasks(accessToken, userId));
          dispatch(todo.actions.setError(null));
        } else {
          dispatch(todo.actions.setItems([]));
          dispatch(todo.actions.setError(data.response));
        }
      });
  };
};

